app.post('/initPortfolio',(req,res)=>{
    if(req.body._id !== "false"){
        Portfolio.findOne({_id:req.body._id}).then((foundPortfolio)=>{
            if(foundPortfolio){
                res.send({exist: true, email: foundPortfolio.email, about: foundPortfolio.about, details: foundPortfolio.details, images: foundPortfolio.images, video: foundPortfolio.video, links:foundPortfolio.links});
            }else{
                res.send({exist:false});
            }
        });
    }else{
        res.send({exist: false});
    }
});

app.post('/portfolio',(req,res)=>{

    const newLinks = new Link(req.body.newLinks);
        
    if(req.body.newImageLink !== "" && req.body.newVideoLink !== ""){
        Portfolio.findOne({email: req.body.email}).then((foundItem)=>{
            Portfolio.updateOne({email:req.body.email},{about: req.body.newAbout, details: req.body.details, images:[...foundItem.images, req.body.newImageLink], video: req.body.newVideoLink, links: newLinks}).then();
        });
    }else if(req.body.newVideoLink !== ""){
        Portfolio.updateOne({email:req.body.email}, {about: req.body.newAbout, details: req.body.details, video:req.body.newVideoLink, links: newLinks}).then();
    }else if (req.body.newImageLink !== ""){
        Portfolio.findOne({email:req.body.email}).then((foundItem)=>{
            Portfolio.updateOne({email:req.body.email},{about: req.body.newAbout, details: req.body.details, images:[...foundItem.images, req.body.newImageLink], links: newLinks}).then();
        });
    }else{
        Portfolio.updateOne({email:req.body.email}, {about: req.body.newAbout, details: req.body.details, links: newLinks}).then();
    }
}
);

app.post('/addPortfolio',(req,res)=>{

    const links = new Link(req.body.link);

    const newPortfolio = new Portfolio({
        email: req.body.email,
        about: req.body.about,
        details: req.body.details,
        images: req.body.images,
        video: req.body.video,
        links: links,
        likes: 0,
        purchases: 0,
        time: new Date()
    });

    newPortfolio.save();
})

