app.post('/discoverLikes', (req,res)=>{

    let hardware = [];
    let software = [];
    let liked = {};

    Portfolio.find().then((foundItems)=>{
        for(let i = 0; i<foundItems.length; i++){
            let element = {id: foundItems[i].id, email:foundItems[i].email, details: foundItems[i].details, image: foundItems[i].images[0], likes: foundItems[i].likes, time: foundItems[i].time, purchases: foundItems[i].purchases};

            if(foundItems[i].category === "software"){
                software.push(element);
            }else{
                hardware.push(element);
            }
        }

        User.findOne({email: req.body.email}).then((foundUser)=>{
            if(foundUser) liked = foundUser.liked;
            res.send({hardware: hardware, software: software, liked: liked});
        })
    });
});

app.post('/discover', (req,res)=>{
    User.findOneAndUpdate({email: req.body.email}, {liked: req.body.newLiked}).then((foundItem)=>{

        if(foundItem){
            let newElements = Object.keys(req.body.newLiked);

            for(let i = 0; i < newElements.length; i++){
                if(!foundItem.liked.has(newElements[i])){
                    Portfolio.updateOne({_id: newElements[i]},{$inc:{likes:1}}).then();
                }
            }

            let i = foundItem.liked.keys();
            let j = foundItem.liked.size;

            while(j--){
                let k = i.next().value;
                if( !req.body.newLiked[k]){
                    Portfolio.updateOne({_id: k},{$inc:{likes: -1}}).then();           
                }
            }
        }
    });
});

app.post("/signup",(req,res)=>{
    User.findOne({email: req.body.email}).then((foundItem)=>{
        if(foundItem){
            res.send({exist: true});
        }else{
            let newUser = new User({
                name: req.body.name,
                email: req.body.email,
                password: req.body.password,
                liked: new Map()
            });
            newUser.save();
            res.send({exist: false});
        }
    })
});

app.post("/signin",(req,res)=>{
    User.findOne({email: req.body.email, password: req.body.password}).then((foundItem)=>{
        if(foundItem){
            Portfolio.findOne({email: foundItem.email}).then(foundPortfolio=>{
                if(foundPortfolio) res.send({incorrect: false, id: foundPortfolio._id});
                else res.send({incorrect: false, id: false })
            });
        }else{
            res.send({incorrect: true});
        }
    });
});


app.post("/buy/getProducts",(req,res)=>{
    Product.find({email: req.body.email}).then(foundItems=>{
        res.send({buyItems: foundItems});
    });
});

app.post("/buy/updateProduct",(req,res)=>{
    const updatedItem = {email: req.body.email, name: req.body.name, description: req.body.description, price: req.body.price, image: req.body.image};
    Product.updateOne({_id: req.body._id},updatedItem).then(()=>{
        res.send({status:"updated"});
    });
});


app.post("/buy/addProduct",(req,res)=>{
    
    const newProduct = new Product({
        email: req.body.email,
        name: req.body.name,
        description: req.body.description,
        price: req.body.price,
        image: req.body.image,
        stock: 12
    });

    newProduct.save();

});

app.post("/cart",(req,res)=>{
    User.findOne({email: req.body.email}).then((foundUser)=>{
        if(foundUser && foundUser.cartItems){
            var cartList = [];
            let cartItemsId = Object.keys(foundUser.cartItems);

            for(let i = 0; i<cartItemsId.length; i++){
                Product.findOne({_id: cartItemsId[i]}).then(foundProduct=>{
                    cartList.push({productDetails: foundProduct, qty: foundUser.cartItems[cartItemsId[i]]});
                    if(i === cartItemsId.length - 1){
                        res.send({cartList : cartList});
                    }
                });
            }
        }else{
            res.send({cartList: []});
        }
    });
});

app.post("/addToCart",(req,res)=>{
    User.findOne({email: req.body.email}).then((foundItem)=>{
        let cartItems = foundItem.cartItems ? foundItem.cartItems : {};
        let newItemsId = Object.keys(req.body.toCart);

        for(let i = 0; i<newItemsId.length; i++){
            if(cartItems[newItemsId[i]]) cartItems[newItemsId[i]] += req.body.toCart[newItemsId[i]];
            else cartItems[newItemsId[i]] = req.body.toCart[newItemsId[i]];
        }

        User.updateOne({email: req.body.email},{cartItems: cartItems}).then();

    });
});


app.post("/deleteCartItem",(req,res)=>{
    User.findOne({email: req.body.email}).then((foundItem)=>{
        let newCartItems = foundItem.cartItems;
        delete newCartItems[req.body.itemId];
        User.updateOne({email: req.body.email},{cartItems: newCartItems}).then(()=>{
            res.send({deleted: true});
        });

    });
});

app.get("/home",(req,res)=>{
    let trending = []; 
    let newProds = [];
    Portfolio.find({},'details images',{sort:{likes: -1}, limit: 10}).then((foundItems)=>{
        for(let i = 0; i<foundItems.length; i++){
            trending.push({_id: foundItems[i]._id, details: foundItems[i].details, image: foundItems[i].images[0]});
        }
        Portfolio.find({},'details images',{sort:{time: 1}, limit: 10}).then((newItems)=>{
            for(let j = 0; j<newItems.length; j++){
                newProds.push({_id: newItems[j]._id, details: newItems[j].details, image: newItems[j].images[0]});
            }

            res.send({newProds: newProds, trending: trending});
        })
    })
});



